#import "AppUserService.h"#import <JSONModelLib.h>#import "AppUser_login.h"#import "AppUser_login.h"#import "AppUser_orderinfo.h"#define AppUser_orderinfoURL [PrexURL stringByAppendingString: @"appUser/orderinfo?memberId=%@&page_status=%@&page_orderSn=%@&page=%@"]#define AppUser_loginURL [PrexURL stringByAppendingString: @"appUser/login?username=%@&password=%@"]@implementation AppUserService-(void)appUser_loginWithUsername:(NSString *)username			andPassword:(NSString *)password			inTabBarController:(UITabBarController *)tabBarController			withDone:(doneWithObject)done{	NSString *urlString = [NSString stringWithFormat:AppUser_loginURL,username,password];	[SVProgressHUD show];	[AppUser_login getModelFromURLWithString:urlString completion:^(AppUser_login *model,		JSONModelError *error){		[SharedAction commonActionWithUrl:urlString				andStatus:model.status				andError:model.message				andJSONModelError:error				andObject:model.data				inTabBarController:tabBarController				withDone:done];	}];}-(void)appUser_orderinfoWithMemberId:(NSString *)memberId			andPage_status:(NSString *)page_status			andPage_orderSn:(NSString *)page_orderSn			andPage:(NSString *)page			inTabBarController:(UITabBarController *)tabBarController			withDone:(doneWithObject)done{	NSString *urlString = [NSString stringWithFormat:AppUser_orderinfoURL,memberId,page_status,page_orderSn,page];	[SVProgressHUD show];	[AppUser_orderinfo getModelFromURLWithString:urlString completion:^(AppUser_orderinfo *model,		JSONModelError *error){		[SharedAction commonActionWithUrl:urlString				andStatus:model.status				andError:model.message				andJSONModelError:error				andObject:model.data				inTabBarController:tabBarController				withDone:done];	}];}@end